import React, {useState} from "react";
import {StyleSheet, View, FlatList, Picker, Text} from "react-native";
import RgbButton from "./RgbButton";

const ChangeColorScreen = () => {
  const [rgb, setRGB] = useState({
    red: 0,
    green: 0,
    blue: 0
  });

  const [pickers,  setPicker] = useState([
    {title: "1", id: "1", select: true,  },
    {title: "10", id: "2", select: false},
    {title: "20", id: "3", select: false},
    {title: "30", id: "4", select: false}
  ]);

  const [curStep, setStep] = useState("1");

  const colors = ["red", "green", "blue"];

  const setState = (color, isPlus) => {
    if (isPlus && rgb[color] === 255) {
      return;
    }

    if (!isPlus && rgb[color] === 0) {
      return;
    }

    setRGB(prev => ({
      ...prev, [color]: isPlus ? prev[color] + +curStep : prev[color] - +curStep
    }))
  }

  return (
    <View style={styles.container}>
      <FlatList
        data={colors}
        renderItem={({item}) => (<RgbButton color={item} setState={setState} value={rgb[item]}/>)}
        keyExtractor={( item) =>  item}
      />

      <View style={{margin: 10, height: 100, weight: 150, backgroundColor: `rgb(${Object.values(rgb)})`}}/>

      <View style={styles.chooseStep}>
        <View style={{flex: 0.5}}>
          <Text style={styles.text}>
            {"Choose step to inc/dec: "}
          </Text>
        </View>

        <View style={{flex: 0.5}}>
          <Picker
            selectedValue={curStep}
            style={{ height: 20, width: 100 }}
            onValueChange={(itemValue, itemIndex) => setStep(itemValue)}
            mode={"dropdown"}
          >
            <Picker.Item label="1" value="1" />
            <Picker.Item label="10" value="10" />
            <Picker.Item label="20" value="20" />
            <Picker.Item label="30" value="30" />
          </Picker>
        </View>
      </View>



    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    // flex: 1,
    flexDirection: "column",
    justifyContent: "space-around",
    // alignItems: "center"
  },

  chooseStep: {
    flexDirection: "row",
    justifyContent: "center",
    alignItems: "center",
    marginBottom: 10
  },

  text: {
    fontWeight: "bold",
    fontSize: 15,
  }


});

export {ChangeColorScreen as default};
